mapper.select:
SELECT * FROM company

    sales_id = #{sales_id}
    AND sector = #{sector}
    AND size > #{sizeMin} 
    AND size  <  #{sizeMax} 

        AND 
        (
            name LIKE '%${query}%'
            OR
            city LIKE '%${query}%'
            OR
            sector LIKE '%${query}%'
            OR
            state_abbr LIKE '%${query}%'
        )


ORDER BY ${sort} ${order}
LIMIT #{take}
OFFSET #{start}

SELECT count(*) FROM company

    sales_id = #{sales_id}
    AND sector = #{sector}
    AND size > #{sizeMin} 
    AND size  <  #{sizeMax} 

        AND 
        (
            name LIKE '%${query}%'
            OR
            city LIKE '%${query}%'
            OR
            sector LIKE '%${query}%'
            OR
            state_abbr LIKE '%${query}%'
        )
