mapper.select:
SELECT * FROM contact c

    LEFT JOIN contact_tag ct
    ON ct.contact_id = c.id


    c.sales_id = #{sales_id}
    AND c.status = #{status}
    AND c.company_id = #{company_id}
    AND c.last_seen > #{last_seen_gte}
    AND c.last_seen  <  #{last_seen_lte}
    AND ct.tag_id = #{tags}

        AND
        (
            c.background LIKE '%${query}%'
            OR 
            c.title LIKE '%${query}%'
            OR 
            CONCAT_WS(' ', c.first_name, c.last_name) LIKE '%${query}%'
        )


ORDER BY c.${sort} ${order}
LIMIT #{take}
OFFSET #{start}

SELECT count(*) FROM contact c

    c.sales_id = #{sales_id}
    AND c.status = #{status}
    AND c.company_id = #{company_id}
    AND c.last_seen > #{last_seen_gte}
    AND c.last_seen  <  #{last_seen_lte}

        AND
        (
            c.background LIKE '%${query}%'
            OR 
            c.title LIKE '%${query}%'
            OR 
            CONCAT_WS(' ', c.first_name, c.last_name) LIKE '%${query}%'
        )
