mapper.select:
SELECT * FROM contact

    sales_id = #{sales_id}
    AND status = #{status}
    AND company_id = #{company_id}
    AND last_seen > #{last_seen_gte}
    AND last_seen  <  #{last_seen_lte}

        AND
        (
            background LIKE '%${query}%'
            OR 
            title LIKE '%${query}%'
            OR 
            CONCAT_WS(' ', first_name, last_name) LIKE '%${query}%'
        )


ORDER BY ${sort} ${order}
LIMIT #{take}
OFFSET #{start}

SELECT count(*) FROM contact

    sales_id = #{sales_id}
    AND status = #{status}
    AND company_id = #{company_id}
    AND last_seen > #{last_seen_gte}
    AND last_seen  <  #{last_seen_lte}

        AND
        (
            background LIKE '%${query}%'
            OR 
            title LIKE '%${query}%'
            OR 
            CONCAT_WS(' ', first_name, last_name) LIKE '%${query}%'
        )
