mapper.select:
SELECT *, _index as 'index', _description as description FROM deal

    company_id = #{company_id}
    AND sales_id = #{sales_id}
    AND type = #{type}
    AND stage = #{stage}

        AND 
        (
            _description LIKE '%${query}%'
            OR
            name LIKE '%${query}%'
        )


ORDER BY ${sort} ${order}
LIMIT #{take}
OFFSET #{start}

SELECT count(*) FROM deal

    company_id = #{company_id}
    AND sales_id = #{sales_id}
    AND type = #{type}
    AND stage = #{stage}

        AND 
        (
            _description LIKE '%${query}%'
            OR
            name LIKE '%${query}%'
        )
